mapper.select:
select id, title, sort, course_id, create_time, update_time
from section
where id = #{id}

select id, title, sort, course_id, create_time, update_time
from section
where course_id = #{courseId}

select id, title, sort, course_id, create_time, update_time
from section
where course_id = #{courseId}

mapper.update:
update section
set

    title = #{title},


    sort = #{sort},


update_time = #{updateTime}
where id = #{id}
